import se.kth.edx.id2203.core.ExercisePrimitives._
import se.kth.edx.id2203.core.Ports._
import se.kth.edx.id2203.validation._
import se.sics.kompics.network._
import se.sics.kompics.sl.{Init, _}
import se.sics.kompics.{ComponentDefinition => _, Port => _,KompicsEvent}

import scala.collection.mutable.Map
import scala.language.implicitConversions

  //The following events are to be used internally by the Atomic Register implementation below
  case class READ(rid: Int) extends KompicsEvent;
  case class VALUE(rid: Int, ts: Int, wr: Int, value: Option[Any]) extends KompicsEvent;
  case class WRITE(rid: Int, ts: Int, wr: Int, writeVal: Option[Any]) extends KompicsEvent;
  case class ACK(rid: Int) extends KompicsEvent;

  /**
    * This augments tuples with comparison operators implicitly, which you can use in your code. 
    * examples: (1,2) > (1,4) yields 'false' and  (5,4) <= (7,4) yields 'true' 
    */
  implicit def addComparators[A](x: A)(implicit o: math.Ordering[A]): o.OrderingOps = o.mkOrderingOps(x);

//HINT: After you execute the latter implicit ordering you can compare tuples as such within your component implementation:
(1,2) <= (1,4);

class ReadImposeWriteConsultMajority(init: Init[ReadImposeWriteConsultMajority]) extends ComponentDefinition {

  //subscriptions

  // Implements
  val nnar = provides[AtomicRegister];

  // Uses:
  val pLink = requires[PerfectLink]; 
  val beb = requires[BestEffortBroadcast];

  //state and initialization

  val (self: Address, n: Int, selfRank: Int) = init match {
    case Init(selfAddr: Address, n: Int) => (selfAddr, n, AddressUtils.toRank(selfAddr))
  };

  var (ts, wr) = (0, 0);
  var value: Option[Any] = None;
  var acks = 0;
  var readval: Option[Any] = None;
  var writeval: Option[Any] = None;
  var rid = 0;
  var readlist: Map[Address, (Int, Int, Option[Any])] = Map.empty
  var reading = false;

  //handlers

  // upon event <nnar, Read> do
  nnar uponEvent {
    case AR_Read_Request() => {
      rid = rid + 1;
      acks = 0;
      readlist.clear;
      reading = true;

      // trigger <beb, Broadcast | [Read, rid]>
      trigger(BEB_Broadcast(READ(rid)) -> beb);

    };

    // upon event <nnar, Write | v> do
    case AR_Write_Request(wval) => { 
      rid = rid + 1;
      writeval = Some(wval);
      acks = 0;
      readlist.clear;
      // trigger <beb, Broadcast | [READ, rid]>
      trigger(BEB_Broadcast(READ(rid)) -> beb);

    }
  }


  beb uponEvent {
    // upon event <beb, Deliver | p, [Read, r]> do
    case BEB_Deliver(src, READ(readID)) => {
        // trigger <pp2p, Send | p, [VALUE, r, ts, wr, val]>
        trigger(PL_Send(src, VALUE(readID, ts, wr, value)) -> pLink);

    }

    // upon event <beb, Deliver | p, [WRITE, r, ts', wr', v']> do
    case BEB_Deliver(src, w: WRITE) => {
        // println(s"Hello World");
        // if((ts', wr') > (ts, wr))
        if ((w.ts, w.wr) > (ts, wr)) {
            // (ts, wr, val) = (ts', wr', v')
            ts = w.ts;
            wr = w.wr;
            value = w.writeVal;
        }
        // trigger <pp2p, Send | p, [ACK, r]>
        trigger(PL_Send(src, ACK(w.rid)) -> pLink);

     /* WRITE YOUR CODE HERE -> finish */
    }
  }


  // upon event <pp2p, Deliver | p, [VALUE, r, ts', wr', v']> do
  pLink uponEvent {
    case PL_Deliver(src, v: VALUE) => {
        if (v.rid == rid) {
          // readlist[p] := (ts', wr', v')
          //readlist = readlist + (src -> (v.ts, v.wr, v.value));
          readlist(src) = (v.ts, v.wr, v.value);
          // if |readlist| > N/2 then
          if (readlist.size > n/2){
              // (maxts, rr, readval) := HIGHEST(readlist)
              val maxvalue = readlist.maxBy(item => item._2._1) // Int
              val highestval = readlist.find(item => item._2._1 == maxvalue._2._1).map(_._2).get;
              var maxts = highestval._1;
              var rr = highestval._2;
              readval = highestval._3;
              readlist.clear;

              var bcastval = readval;

              if (reading == true){
                  // bcastval := readval
                  bcastval = readval;
              }
              else{
                  // rr := RANK(self)
                  rr = selfRank;
                  // maxts := maxts+1
                  maxts = maxts + 1;
                  // bcastval := writeval
                  bcastval = writeval;
              }
              // trigger <beb, Broadcast | [WRITE, rid, maxts, rr, bcastval]>
              trigger(BEB_Broadcast(WRITE(rid, maxts, rr, bcastval)) -> beb);

          }
      }
    }

    // upon event <pp2p, Deliver | p, [ACK, r]> do
    case PL_Deliver(src, v: ACK) => {
        println(s"Hello")
        if (v.rid == rid) {
          acks = acks + 1;
          // N = |pie|
          if (acks > n/2){
              acks = 0;
              if (reading == true){
                  println(s"read_response");
                  reading = false;
                  // trigger <nnar, ReadReturn | readval>
                  trigger(AR_Read_Response(readval) -> nnar);
              }
              else{
                  // trigger <nnar, WriteReturn>
                  println(s"write_response");
                  trigger(AR_Write_Response() -> nnar);
              }
          } 

        }
    }

  }
}
